import uuid from 'uuid';
import { createStore } from 'redux';

const initialState = {
    notes: [],
};

const ADD_NOTE = '[notes] Add a note to the application';

const ADD_ACTION = {
    type: ADD_NOTE,
    payload: {
      id: uuid(),
      title,
      note
    }
};

const noteReducer = (state = initialState, action) => {
    switch(action.type) {
        case 'ADD_NOTE':
          return state.notes.concat(action.payload);
        default:
          return state;
      }
};



const store = createStore(noteReducer);

export {
    store,
    ADD_ACTION,
    ADD_NOTE
};
