import React, { Component } from 'react';

import { store } from '../reducers/noteReducers';
import { ADD_ACTION } from '../actions/noteActions';


class NoteForm extends Component {
    componentDidMount(){

    }
    render(){
        return(
            <div>
                <h1>Create Note</h1>
                <form onSubmit = { () =>  store.dispatch(ADD_ACTION) }>
                    <input required type="text" placeholder="Enter Note Title" /><br /><br />
                    <textarea required rows="5" cols="28" placeholder="Enter Note Information" /><br /><br />
                    <button >Save Note</button>
                </form>
            </div>
        )
    }
}

export default NoteForm
