import { initialState } from '../actions/noteAction';
import ADD_NOTE from '../actions/noteAction';



const noteReducer = (state = initialState, action) => {
    switch(action.type) {
        case 'ADD_NOTE':
          return state.notes.concat(action.payload);
        default:
          return state;
      }
};
export default noteReducer;
