import React, {Component} from 'react';
import { store } from '../reducers/noteReducers';
// import { ADD_ACTION } from '../actions/noteActions';
class AllNotes extends Component {
    state = {
        notes: []
    }
    componentDidMount(){
        store.subscribe(() => {
            this.setState({ notes: store.getState().notes });
        });
    }
    render(){
        const { notes } = this.state;

        {notes.map((note) => {
            return(
                <div>
                    <p>{note.title}</p>
                    <p>{note.notebody}</p>

                </div>
            )
        })
        }
    }
}

export default AllNotes
