import React, {
    Component
} from 'react';

import {
    connect
} from 'react-redux';

class Note extends Component {
    render() {
        return ( 
        <div className = "post">
            <h2 className = "post_title" > {this.props.note.title}</h2> 
            <p className = "post_message" > {this.props.note.notecontent}</p> 
            <div className = "control-buttons" >
                <button className = "edit" onClick = {() => this.props.dispatch({
                    type: 'EDIT_NOTE',
                    id: this.props.note.id
                })}> Edit </button> 
                <button className = "delete" onClick = {() => this.props.dispatch({
                    type: 'DELETE_NOTE',
                    id: this.props.note.id
                })}> Delete </button> 
            </div > 
        </div>
        );
    }
}
export default connect()(Note);
