};

const ADD_NOTE = '[notes] Add a note to the application';

const ADD_ACTION = {
    type: ADD_NOTE,
    payload: {
      id: uuid(),
      title,
      note
    }
};

export {
    initialState,
    ADD_NOTE,
    ADD_ACTION
};
