import React,{ Component } from 'react';
import uuid from 'uuid';
import { connect } from 'http2';

console.log(uuid())
class NoteForm extends Component {

    handleSubmit = (event) => {
        event.preventDefault();
        const title = this.getTitle.value;
        const notebody =  this.getBody.value;
        const payload = {
          id: uuid(),
          title,
          notebody
        }
        console.log(payload)
    }

    render(){
        return(
            <div>
                <h1>Create Note</h1>
                <p>
                </p>
                <form onSubmit={this.handleSubmit}>
                <input required type="text" ref={(input)=>this.getTitle = input} placeholder="Enter Note Title"/>
                <br /><br />
                <textarea required rows="5" ref={(input)=>this.getBody= input} cols="28" placeholder="Enter Note" />
                <br /><br />
                <button>Save Note</button>
                </form>
            </div>
        );
    }
};

export default connect() (NoteForm);
