import { ADD_NOTE, ADD_ACTION } from '../actions/noteActions';

import { createStore } from 'redux';

const initialState = {
    notes: {}
}

const addNoteReducer = ( state = initialState, action ) => {
    switch(action.type){
        case ADD_NOTE:
            return Object.assign( state, {}, { notes: action.payload, });
        default:
            return state;

    }

};

const store = createStore(addNoteReducer, initialState);

export const dispatchAddReducer = () => {
    return store.dispatch(ADD_ACTION);
}
