import uuid from 'uuid';
import { createStore } from 'redux';

const initialState = {
    notes: {},
};

const ADD_NOTE = '[notes] Add a note to the application';

const ADD_ACTION = {
    type: ADD_NOTE,
    payload: {
      id: uuid(),
      title:'Nice stuff',
      note:'cool'
    }
};

const noteReducer = (state = initialState, action) => {
    switch(action.type) {
        case 'ADD_NOTE':
          return Object.assign(
                state, {}, {notes : action.payload}
          );
        break;

        default:
          return state;
        break;
      }
};



const store = createStore(noteReducer, initialState);

export {
    store,
    ADD_ACTION,
    ADD_NOTE
};
