import React, { Component } from 'react';

import { store } from '../reducers/noteReducers';
import { ADD_ACTION } from '../actions/noteActions';


class NoteForm extends Component {
    state = {
        notes: []
    }


    componentDidMount(){
        store.subscribe(() => {
            this.setState({ notes: store.getState().notes });

        });

    }
    render(){
        const { notes } = this.state;
        return(
            <div>
                <h1>Create Note</h1>
                <form>
                    <input required type="text" placeholder="Enter Note Title" ref={(input)=>this.getTitle = input}/><br /><br />
                    <textarea required rows="5" cols="28" placeholder="Enter Note Information" ref={(input)=>this.getBody = input}/><br /><br />
                    <button onClick = { () => store.dispatch(ADD_ACTION)}>Save Note</button>
                </form>

                {notes.map((note) => {
                    return(
                        <div key={note.id}>

                            <p>{note.title}</p>
                            <p>{note.notebody = notebody}</p>

                        </div>
                    )
                })
                }
            </div>
        )
    }
}

export default NoteForm
