import { ADD_NOTE, initialState } from '../actions/noteActions';



const addNoteReducer = ( state = initialState, action ) => {
    switch(action.type){
        case ADD_NOTE:
            return Object.assign( state, {}, { notes: action.payload, });
        default:
            return state;

    }

}
