import React, {
    Component
} from 'react';
import {
    uuid
} from 'uuid';


class NoteForm extends Component {

    handleSubmit = (event) => {
        event.preventDefault();
        const title = this.getTitle.value;
        const notebody = this.getBody.value;
        const payload = {
            id: uuid(),
            title,
            notebody
        }
    }

    render() {
        return ( <
            div >
            <
            h1 > Create Note < /h1> <
            p >
            <
            /p> <
            form onSubmit = {
                this.handleSubmit
            } >
            <
            input required type = "text"
            ref = {
                (input) => this.getTitle = input
            }
            placeholder = "Enter Post Title" / >
            <
            br / > < br / >
            <
            textarea required rows = "5"
            ref = {
                (input) => this.getMessage = input
            }
            cols = "28"
            placeholder = "Enter Post" / >
            <
            br / > < br / >
            <
            button > Post < /button> <
            /form> <
            /div>
        );
    }
};

export default NoteForm;
