import React,{ Component } from 'react';
import { store, ADD_ACTION } from '../reducers/noteReducer';



class NoteForm extends Component {
    componentDidMount(){
        store.subscribe(() => {
            console.log(store.getState());
        });
    }
    render(){
        return(
            <div>
                <h1>Create Note</h1>
                <form>
                    <input required type="text" placeholder="Enter Post Title" /><br /><br />
                    <textarea required rows="5" cols="28" placeholder="Enter Post" /><br /><br />
                    <button onClick={store.dispatch(ADD_ACTION)}>Save</button>
                </form>
            </div>
        );
    }
};

export default NoteForm;
